<?php    function post_captcha($user_response) {        $fields_string = '';        $fields = array(            'secret' => '6Lc8qswcAAAAAMxTldJHIjvjJsxjLA7f_oSNZi6X',            'response' => $user_response        );        foreach($fields as $key=>$value)        $fields_string .= $key . '=' . $value . '&';        $fields_string = rtrim($fields_string, '&');        $ch = curl_init();        curl_setopt($ch, CURLOPT_URL, 'https://www.google.com/recaptcha/api/siteverify');        curl_setopt($ch, CURLOPT_POST, count($fields));        curl_setopt($ch, CURLOPT_POSTFIELDS, $fields_string);        curl_setopt($ch, CURLOPT_RETURNTRANSFER, True);        $result = curl_exec($ch);        curl_close($ch);        return json_decode($result, true);    }    $res = post_captcha($_POST['g-recaptcha-response']);    if (!$res['success']) {		header('Location: https://www.revolutionfc.ca/contact.php?m=15');    }	else	{		unset($_GET);		if(!empty($_POST))		{			$valid = true;			if(empty($_POST['name']))			{				$valid = false;				$errorName = 'Indiquez votre nom';				}			if(empty($_POST['email']))			{				$valid = false;				$errorEmail = 'Indiquez votre courriel';				}			if(!empty($_POST['email']) && !filter_var($_POST['email'],FILTER_VALIDATE_EMAIL))			{				$valid = false;				$errorEmail_FR = 'Adresse courriel invalide';				}			if(empty($_POST['message']))			{				$valid = false;				$errorMessage = 'Indiquez votre message';				}			if($valid)			{				$name=utf8_decode($_POST['name']);				$email=utf8_decode($_POST['email']);				$message=utf8_decode($_POST['message']);				$subject_email=utf8_decode($_POST['subject']);				$subject = "Revolution FC - Nouveau message depuis le site internet";				/* Destinataire */                $to = $_POST['departements'];				// More headers				$headers .= 'From: ' .$email. "\r\n";				/* Construction email */				$msg.= 'Message de '.$name."\r\n";				$msg.= $subject_email."\r\n";				$msg.= 'Adresse courriel '.$email."\r\n";				$msg.= '***********'."\r\n";				$msg.= $message."\r\n";				/* envoi mail */				mail($to,$subject,$msg,$headers);				header('Location: https://www.revolutionfc.ca/contact.php?m=14');			}		}    }    	?>