<?php	session_start();	include("../_includes/_revolutionfc.inc");	if(get_magic_quotes_gpc())	{		$login = stripslashes($_POST['zt_identifiant']);		$pass  = stripslashes($_POST['zt_password']);	}	else	{		$login = $_POST['zt_identifiant'];		$pass  = $_POST['zt_password'];	}            /* ======== LOGIN ============== */        $stmt = $db->prepare('SELECT * FROM USERS WHERE login = ?');        if ($stmt->execute([$login])) {                $user = $stmt->fetch(PDO::FETCH_ASSOC);        if (!$user || !password_verify($pass, $user['password'])) {            header('Location: https://www.revolutionfc.ca/admin/login?m=5');            exit();        }                $actif = $user['actif'];        $login = $user['login'];        $email = $user['email'];        $type = $user['type'];        $id_user = $user['id_user'];                $_SESSION['auth'] = $actif;        $_SESSION['utilisateur'] = $login;        $_SESSION['email'] = $email;        $_SESSION['type'] = $type;        $_SESSION['user'] = $id_user;                if ($actif != 1) {            header('Location: https://www.revolutionfc.ca/?m=6');            exit();        } else {            if ($type >= 3) {                header('Location: https://www.revolutionfc.ca/admin/index');                exit();            } else {                header('Location: https://www.revolutionfc.ca/?m=11');                exit();            }        }    } else {        header('Location: https://www.revolutionfc.ca/admin/login?m=5');    }?>