<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        // Check if zt_title is set and not empty    if(isset($_POST['zt_title']) && !empty($_POST['zt_title'])) {        $zt_title = nl2br(stripslashes($_POST['zt_title']));                // Prepare and execute the SQL query to insert the album title into the database        $stmt = $db->prepare('INSERT INTO ALBUMS (album_title) VALUES (?)');        $execute = $stmt->execute([addslashes($zt_title)]);                // Check if the SQL query was executed successfully        if ($execute) {            // Get the last inserted id_album            $lastInsertedId = $db->lastInsertId();                        // Close the database cursor            $stmt->closeCursor();                        // Create the folder on the remote server            $folderPath = $_SERVER['DOCUMENT_ROOT'] . '/img/_albums/' . $lastInsertedId; // Define the folder path            if (!file_exists($folderPath)) {                // Check if the folder doesn't already exist                if (mkdir($folderPath, 0777, true)) {                    // Folder created successfully                    retour_erreur(66, 661);                } else {                    // Error creating folder                    retour_erreur(66, 663);                }            } else {                // Folder already exists                retour_erreur(66, 664); // or 662 depending on your preference            }        } else {            // Error executing SQL query            retour_erreur(66, 662);        }    } else {        // Handle the case where zt_title is not set or empty        retour_erreur(66, 665); // or any appropriate error code    }?>