<?php	include("../_includes/session_test.inc");	include("../_includes/_revolutionfc.inc");        // Check if the form is submitted and contains the required fields.    if ($_SERVER['REQUEST_METHOD'] === 'POST') {        $zt_title = isset($_POST['zt_title']) ? nl2br(stripslashes($_POST['zt_title'])) : null;        $element = isset($_POST['element']) ? $_POST['element'] : null;                // Validate inputs (e.g., check for required fields, sanitize data).        if (empty($zt_title) || $element === null) {            retour_erreur(66, 7);        }                // Update the sponsor information in the database.        $updateResult = updateAlbumInfo($element, $zt_title);                if ($updateResult) {            retour_erreur(66, 3); // Successful update.        } else {            retour_erreur(66, 4); // Handle database update errors.        }            } else {        retour_erreur(66, 7); // Handle form submission errors.    }            // Function to update sponsor information in the database.    function updateAlbumInfo($element, $zt_title) {        global $db;        $stmt = $db->prepare('UPDATE ALBUMS SET album_title = ? WHERE id_album = ?');        return $stmt->execute([$zt_title, $element]);    }?>