<?php    // Include necessary files and start the session if not already started    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        // Check if the form was submitted    if ($_SERVER["REQUEST_METHOD"] === "POST") {        // Extract and sanitize input values        $zt_adress = isset($_POST['zt_adress']) ? nl2br(stripslashes($_POST['zt_adress'])) : "";        $ta_hours = isset($_POST['ta_hours']) ? nl2br(stripslashes($_POST['ta_hours'])) : "";        $zt_phone = isset($_POST['zt_phone']) ? $_POST['zt_phone'] : "";        $zt_map = isset($_POST['zt_map']) ? $_POST['zt_map'] : "";        $zt_email_1 = isset($_POST['zt_email_1']) ? $_POST['zt_email_1'] : "";        $zt_email_2 = isset($_POST['zt_email_2']) ? $_POST['zt_email_2'] : "";                // Prepare and execute the SQL update statement        $stmt = $db->prepare('UPDATE CONFIG_contact SET adress_1 = ?, club_phone = ?, club_map = ?, club_email = ?, club_email_2 = ?, club_hours = ? WHERE id_config = 1');        $execute = $stmt->execute([            addslashes($zt_adress),            $zt_phone,            $zt_map,            addslashes($zt_email_1),            addslashes($zt_email_2),            $ta_hours // Do not use addslashes on $ta_hours        ]);                if ($execute) {            // Success: Redirect or display a success message            $stmt->closeCursor();            retour_erreur(3, 3);        } else {            // Error: Handle the error, e.g., display an error message or log it            retour_erreur(3, 4);        }    } else {        // Form was not submitted, handle accordingly        retour_erreur(3, 4);    }?>