<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        unset($_GET);        // Get and sanitize form inputs    $zt_registration = isset($_POST['zt_registration']) ? htmlspecialchars($_POST['zt_registration']) : '';    $zt_store = isset($_POST['zt_store']) ? htmlspecialchars($_POST['zt_store']) : '';    $zt_tax = isset($_POST['zt_tax']) ? htmlspecialchars($_POST['zt_tax']) : '';    $facebook = isset($_POST['zt_fb']) ? htmlspecialchars($_POST['zt_fb']) : '';    $instagram = isset($_POST['zt_in']) ? htmlspecialchars($_POST['zt_in']) : '';        $stmt = $db->prepare('UPDATE CONFIG_links SET                        link_registration = ?,                        link_store = ?,                        link_tax_receipt = ?,                        link_facebook = ?,                        link_instagram = ?,                    WHERE id_config = 1');    $execute = $stmt->execute([        $zt_registration,        $zt_store,        $zt_tax,        $facebook,        $instagram    ]);        if ($execute) {        $stmt->closeCursor();        retour_erreur(3, 3); // Successful update    } else {        retour_erreur(3, 4); // Update failed    }?>