<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");    include("../_includes/_fileUpload.inc"); // Include the file with the handleFileUpload function            unset($_GET);        if (isset($_POST['zt_title'])) {        $zt_title = nl2br(stripslashes($_POST['zt_title']));    } else {        retour_erreur(31, 7);    }    if (isset($_POST['subcategory_select'])) {        $subcategory = $_POST['subcategory_select'];    } else {        retour_erreur(31, 7);    }        $element = $_POST['element'];    $database = $_POST['db-name'];    $page = '31';        if ($_FILES['f_document']['size'] == 0) {                $stmt = $db->prepare("UPDATE $database SET doc_subcategory =?, doc_title = ? WHERE id_doc = ?");        $execute = $stmt->execute([            $subcategory,            addslashes($zt_title),            $element        ]);                if ($execute) {            $stmt->closeCursor();            retour_erreur(31, 3);        } else {            retour_erreur(31, 4);        }            } else {        // Handle file upload        $dossier = "docs/";        $destination = handleDocUpload($_FILES['f_document'], $dossier, $page);                $stmt = $db->prepare("UPDATE $database SET doc_subcategory = ?, doc_title = ?, doc_link = ? WHERE id_doc = ?");        $execute = $stmt->execute([            $subcategory,            addslashes($zt_title),            $destination,            $element        ]);                if ($execute) {            $stmt->closeCursor();            retour_erreur(31, 3);        } else {            retour_erreur(31, 4);        }    }?>