<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        // Check if all the required POST parameters are set    $requiredFields = ['ld_city', 'zt_name', 'zt_date', 'zt_start'];    foreach ($requiredFields as $field) {        if (!isset($_POST[$field])) {            retour_erreur(83, 7);        }    }        $ld_city = $_POST['ld_city'];    $zt_name = nl2br(stripslashes($_POST['zt_name']));    $zt_date = $_POST['zt_date'];    $zt_date_end = isset($_POST['zt_date_end']) ? $_POST['zt_date_end'] : null; // Set to null if empty    $zt_start = $_POST['zt_start'];    $zt_end = isset($_POST['zt_end']) ? $_POST['zt_end'] : null; // Set to null if empty    $actif = '1';        $stmt2 = $db->prepare('INSERT INTO EVENTS (event_name, event_venue, event_date, event_date_end, event_start_time, event_end_time, is_actif) VALUES (?, ?, ?, ?, ?, ?, ?)');    $execute = $stmt2->execute([$zt_name, $ld_city, $zt_date, $zt_date_end, $zt_start, $zt_end, $actif]);        if ($execute) {        $stmt2->closeCursor();        retour_erreur(63, 641);    } else {        $errorInfo = $stmt2->errorInfo();        print_r($errorInfo); //        retour_erreur(63, 642);    }?>