<?php	include("../_includes/session_test.inc");	include("../_includes/_revolutionfc.inc");	include("../_includes/_fileUpload.inc");		unset($_GET);        // Check if the required POST parameters are set    $requiredFields = ['zt_titre', 'zt_intro', 'ta_news', 'ld_section'];        foreach ($requiredFields as $field) {        if (!isset($_POST[$field])) {            retour_erreur(11, 7);        }    }        $zt_titre = nl2br(stripslashes($_POST['zt_titre']));    $zt_intro = nl2br(stripslashes($_POST['zt_intro']));    $ta_news = nl2br(stripslashes($_POST['ta_news']));    $ta_news_clean = str_replace("<br /><br />", "<br />", $ta_news);    $section = $_POST['ld_section'];        $date_actu = date("Y-m-d");            // Define the valid file extensions    $extensions_valides = array('jpg', 'jpeg', 'png');    $page = '11';    $error_code = '1024';        // Handle file upload    $dossier = "img/_news/";    $destination = handleFileUpload($_FILES['f_photo_actu'], $dossier, $extensions_valides, $page, $error_code);        $stmt = $db->prepare('INSERT INTO ACTUALITE (titre, date_actu, intro, texte, photo, id_section) VALUES (?, ?, ?, ?, ?, ?)');    $execute = $stmt->execute([        $zt_titre,        $date_actu,        $zt_intro,        $ta_news_clean,        $destination,        $section    ]);    if ($execute) {        $stmt->closeCursor();        retour_erreur(11,101);    } else {        retour_erreur(11,102);    }?>