<?php	include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");    include("../_includes/_fileUpload.inc"); // Include the file with the handleFileUpload function		unset($_GET);        $name = isset($_POST['zt_name']) ? nl2br(stripslashes($_POST['zt_name'])) : retour_erreur(21, 23);    $legend = isset($_POST['zt_desc']) ? nl2br(stripslashes($_POST['zt_desc'])) : retour_erreur(21, 23);    $email = isset($_POST['zt_email']) ? nl2br(stripslashes($_POST['zt_email'])) : retour_erreur(21, 23);    $element = $_POST['element'];        // Define the valid file extensions    $extensions_valides = array('jpg', 'jpeg', 'png');    $page = '21';    $error_code = '1024';        if ($_FILES['f_ppl_img']['size'] == 0) {        // If no new image is uploaded, update the other fields without changing the image column        $stmt = $db->prepare('UPDATE MEMBERS_CA SET member_name = ?, member_legend = ?, member_email = ? WHERE id_member = ?');        $execute = $stmt->execute([            $name,            $legend,            $email,            $element        ]);                if ($execute) {            $stmt->closeCursor();            retour_erreur(21, 3);        } else {            retour_erreur(21, 4);        }            } else {        // Handle file upload        $dossier="img/_people/";        $destination = handleFileUpload($_FILES['f_ppl_img'], $dossier, $extensions_valides, $page, $error_code);                $stmt = $db->prepare('UPDATE MEMBERS_CA SET member_name = ?, member_legend = ?, member_email = ?, member_img = ? WHERE id_member = ?');        $execute = $stmt->execute([            $name,            $legend,            $email,            $destination,            $element        ]);                if ($execute) {            $stmt->closeCursor();            retour_erreur(21, 3);        } else {            retour_erreur(20, 4);        }    }?>