<?php	include("../_includes/session_test.inc");	include("../_includes/_revolutionfc.inc");        if ($_SERVER['REQUEST_METHOD'] === 'POST') {        // Retrieve the program order from the POST data        $programOrder = json_decode($_POST['programOrder']);                // Update the program order in the database        $stmt = $db->prepare("UPDATE PROGRAMS SET p_order = ? WHERE id_program = ?");                // Iterate over the program order array and update the database        foreach ($programOrder as $order => $programId) {            // Execute the prepared statement with the current program order and program ID            $stmt->execute([$order, $programId]);        }                // Close the prepared statement        $stmt->closeCursor();                // Return a success message or response        echo json_encode(['success' => true, 'message' => 'l\'ordre des programmes a été mis à jour correctement']);    } else {        // Return an error response if the request method is not POST        echo json_encode(['success' => false, 'message' => 'Il y a une erreur dans votre action']);    }    ?>