<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        // Ensure required POST data is set    $requiredFields = ['num_category', 'num_tournoi', 'zt_category', 'zt_soccer', 'zt_fee', 'zt_link'];    foreach ($requiredFields as $field) {        if (!isset($_POST[$field])) {            header('Location: https://www.revolutionfc.ca/admin/index.php?p=82&t=' . $_POST['num_tournoi'] . '&m=7');            exit;        }    }        // Sanitize and retrieve POST data    $categoryvoulu = $_POST['num_category'];    $tournoivoulu = $_POST['num_tournoi'];    $category = nl2br(stripslashes($_POST['zt_category']));    $soccer = nl2br(stripslashes($_POST['zt_soccer']));    $fee = nl2br(stripslashes($_POST['zt_fee']));    $link = nl2br(stripslashes($_POST['zt_link']));        // Update database    $stmt = $db->prepare('UPDATE TOURNAMENT_CATEGORIES SET t_category_age = ?, t_category_type = ?, t_category_fee = ?, t_category_link = ? WHERE id_t_category = ?');    $execute = $stmt->execute([$category, $soccer, $fee, $link, $categoryvoulu]);        if ($execute) {        $stmt->closeCursor();        header('Location: https://www.revolutionfc.ca/admin/index.php?p=82&t=' . $tournoivoulu . '&m=3');    } else {        header('Location: https://www.revolutionfc.ca/admin/index.php?p=82&t=' . $tournoivoulu . '&m=4');    }?>