<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");// Check required POST fields    $requiredFields = ['zt_name' => 23, 'zt_pos' => 23, 'zt_desc' => 23, 'ta_quote' => 23, 'd_year' => 23, 'd_month' => 23];    foreach ($requiredFields as $field => $errorCode) {        if (!isset($_POST[$field]) || trim($_POST[$field]) === '') {            retour_erreur(28, $errorCode);        }    }// Fetching the year and month from the form submission    $year = $_POST['d_year'];    $month = $_POST['d_month'];// First, check if there's already a record with the same month and year    $checkStmt = $db->prepare("SELECT COUNT(*) FROM VOLUNTEER_hotm WHERE hotm_year = ? AND hotm_month = ?");    $checkStmt->execute([$year, $month]);    $exists = $checkStmt->fetchColumn() > 0;        if ($exists) {        // If a record already exists for this month and year, return a specific error        retour_erreur(29, 293); // 293 is an example error code for "record already exists for this month and year"    } else {        // Prepare data for database insertion        $name = $_POST['zt_name'];        $pos = $_POST['zt_pos'];        $desc = $_POST['zt_desc'];        $quote = $_POST['ta_quote'];                // Proceed with the insert operation since there's no duplicate month-year        $stmt = $db->prepare("INSERT INTO VOLUNTEER_hotm (hotm_name, hotm_pos, hotm_desc, hotm_quote, hotm_year, hotm_month)                          VALUES (?, ?, ?, ?, ?, ?)");                if ($stmt->execute([            addslashes($name),            addslashes($pos),            addslashes($desc),            addslashes($quote),            $year,            $month        ])) {            retour_erreur(29, 291); // Assuming 291 is the success code        } else {            retour_erreur(29, 292); // Assuming 292 is a general failure code        }    }