<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");    include("../_includes/_fileUpload.inc");    // Check required POST fields    $requiredFields = ['ta_benevoles' => 23, 'element' => 'Provide specific error code'];    foreach ($requiredFields as $field => $errorCode) {        if (!isset($_POST[$field]) || trim($_POST[$field]) === '') {            retour_erreur(28, $errorCode);        }    }    // Prepare data for database insertion    $overwrite = isset($_POST['switch_overwrite']) && $_POST['switch_overwrite'] == 'on' ? 1 : 0;    $hotm = $_POST['ld_hotm'];    $text = $_POST['ta_benevoles'];    $element = $_POST['element'];        try {        $query = 'UPDATE VOLUNTEER_xp SET hotm_overwrite = ?, hotm_manual =?, xp_testimony = ? WHERE id_volunteer_xp = ?';        $params = [$overwrite, $hotm, $text, $element];                $stmt = $db->prepare($query);        $stmt->execute($params);        $stmt->closeCursor();                retour_erreur(28,3); // Assuming success code    } catch (Exception $e) {        // Log the exception details to debug database errors        retour_erreur(28, 4); // Assuming failure code    }