<?php	include("../_includes/session_test.inc");	include("../_includes/_revolutionfc.inc");?><!doctype html><html><head><meta charset="UTF-8"><title>Membres du CA</title><?php	include("../_includes/head.inc");    echo "<body>";	include("../_includes/header.inc");?><!-- START OF THE CONTACT SECTION --><section>	<article>		<h2 class="section-title">Conseil d'Administration du club</h2>		<ul class="grid-club-staff">            <?php    $stmtList = $db->prepare('SELECT * from MEMBERS_CA ORDER BY                    CASE                        WHEN member_function = "Président" THEN 1                        WHEN member_function = "Vice-Président" THEN 2                        WHEN member_function = "Trésorier" THEN 3                        WHEN member_function = "administrateur" THEN 4                        ELSE 5                    END, id_member ASC');    $stmtList->execute();        $resultsList = $stmtList->fetchAll(PDO::FETCH_ASSOC);    foreach ($resultsList as $result) {        $function = nl2br(stripslashes($result['member_function']));        $name = nl2br(stripslashes($result['member_name']));                // Check if member_img is not empty        $img = $result['member_img'];        $imgUrl = "../".$img;        $imgContainer = !empty($img) ? "<div class='people-vignette-img' style='background-image: url($imgUrl)'></div>" : '';                // Check if member_legend is not empty        $legend = nl2br(stripslashes($result['member_legend']));        $legendContainer = !empty($legend) ? "<div class='list-people-legend'>$legend</div>" : '';                // Check if member_email is not empty        $email = nl2br(stripslashes($result['member_email']));        $emailContainer = !empty($email) ? "<p class='people-contact'><a href='mailto:$email'>$email</a></p>" : '';                echo "			<li class='people-vignette'>                $imgContainer                <div class='people-info'>                    <h3 class='people-position'>$function</h3>                    <h4 class='people-name'>$name</h4>                    $emailContainer                </div>            </li>                ";            }            ?>		</ul>	</article></section><!-- END OF THE CONTACT SECTION -->			<?php include("../_includes/footer.inc") ?>