<?php    include("../_includes/session_test.inc");    include("../_includes/_revolutionfc.inc");        $element = $_GET['id'];        $stmt = $db->prepare('SELECT * FROM EVENTS, FIELDS, FIELDS_cities WHERE event_venue = id_field AND field_data_city = id_city AND id_event = ?');    $stmt->execute([$element]);        $events = []; // Array to store the results        while ($row = $stmt->fetch()) {        setlocale(LC_TIME, "fr_FR");                $title = nl2br(stripslashes($row['event_name']));                $date = date('Y-m-d', strtotime($row['event_date']));        $date_end = date('Y-m-d', strtotime($row['event_date_end']));        if (!empty($date_end) && $date_end != '0000-00-00') {            $date_text = strftime("%d %B", strtotime($date)) . ' - ' . strftime("%d %B %G", strtotime($date_end));        } else {            $date_text = strftime("%d %B", strtotime($date));        }        $date_text = nl2br(stripslashes(utf8_encode($date_text)));        $startTime = date('H:i', strtotime($row['event_start_time']));        $endTime = date('H:i', strtotime($row['event_end_time']));                $venue = $row['field_name'];        $address = $row['field_address'];        $city = nl2br(stripslashes($row['city_name']));                $events[] = [            'title' => $title,            'date' => $date,            'date_end' => $date_end,            'date_text' => $date_text,            'startTime' => $startTime,            'endTime' => $endTime,            'venue' => $venue,            'address' => $address,            'city' => $city        ];    }?>    <!doctype html><html><head><meta charset="UTF-8"><title><?php echo $title ?></title><?php    include("../_includes/head.inc");    echo "<body>";    include("../_includes/header.inc");?>            <section class="p-events">                <article class="wrapper wrapper-text">            <div class="event-header">                <h1 class="event-title"><?php echo $title ?></h1>                <h5 class="event-date"><?php echo $date_text ?><br/><?php echo $venue ?></h5>            </div>        </article>                <article class="wrapper">            <div class="event-content">                <h4>Date & Time</h4>                <div class="event-detail">                    <div class="event-detail-el">                        <div class="event-detail-label">Dates de l'évènement</div>                        <div class="event-detail-text"><?php echo $date_text ?></div>                    </div>                    <div class="event-detail-el">                        <div class="event-detail-label">Heure de début</div>                        <div class="event-detail-text"><?php echo $startTime ?></div>                    </div>                    <?php                        if (!empty($endTime) && $endTime != '00:00') {                            echo "                    <div class='event-detail-el'>                        <div class='event-detail-label'>Heure de fin</div>                        <div class='event-detail-text'>$endTime</div>                    </div>";                        }                    ?>                </div>            </div>            <div class="event-content">                <h4>Location</h4>                <div class="event-detail">                    <div class="event-detail-el">                        <div class="event-detail-label">Lieu</div>                        <div class="event-detail-text"><?php echo $venue ?></div>                    </div>                    <div class="event-detail-el">                        <div class="event-detail-label">Adresse</div>                        <div class="event-detail-text"><?php echo $address ?></div>                    </div>                    <div class="event-detail-el">                        <div class="event-detail-label">Ville / Arrondissement</div>                        <div class="event-detail-text"><?php echo $city ?></div>                    </div>                </div>            </div>                        <div class="w-btn">                <a class="btn-tertiary btn-text icon i-long-arrow-table" href="/evenements/">Voir tous nos évènements</a>            </div>        </article>        </section><?php include("../_includes/footer.inc") ?>