                                      // Check if member_email is not empty                    $email = nl2br(stripslashes($result['member_email']));                    $emailContainer = !empty($email) ? "<p class='people-contact'><a href='mailto:$email'>$email</a></p>" : '';                                        echo "			<li class='people-vignette'>                $imgContainer                <div class='people-info'>                    <h3 class='people-position'>$function</h3>                    <h4 class='people-name'>$name</h4>                    $emailContainer                </div>            </li>                ";                }                                    ?>		</ul>	</article></section><!-- END OF THE CONTACT SECTION -->			<?php include("_includes/footer.inc") ?>